import React, { Component } from 'react';
import BootstrapModal from '../partials/BootstrapModal';

class UserAddEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            email: "",
            password: "",
            first_name: "",
            last_name: "",
            groups: [],
            error_message: {},
            msg_success: "",
            modalTitle: (this.props.addFlag === true ? 'User Add' : 'User Edit')

        };

        this.onChangeFirstName = this.onChangeFirstName.bind(this);
        this.onChangeLastName = this.onChangeLastName.bind(this);
        this.onChangeUserName = this.onChangeUserName.bind(this);
        this.onChangeEmailName = this.onChangeEmailName.bind(this);
    }

    onChangeFirstName(e) {
        this.setState({
            first_name: e.target.value
        });
    }

    onChangeLastName(e) {
        this.setState({
            last_name: e.target.value
        });
    }

    onChangeUserName(e) {
        this.setState({
            username: e.target.value
        });
    }
    onChangeEmailName(e) {
        this.setState({
            email: e.target.value
        });
    }

    onChangeGroupName(e) {
        this.setState({
            groups: e.target.value
        });
    }

    onChangeStatus(e) {

        if (e.target.value === 'Active') {
            this.setState({
                status: true
            });
        } else {
            this.setState({
                status: false
            });
        }

    }

    render() {
        return (
            <div>
                <BootstrapModal openModal={this.props.openModal} modalHandler={this.props.modalHandler} modalTitle={this.state.modalTitle}>
                <form>
                        <div className="form-row">
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1"> First Name</label>
                                    <input type="text" className="form-control"
                                        id="first_name" aria-describedby="emailHelp"
                                        onChange={this.onChangeUserName}
                                        value={this.state.first_name} />
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1"> Last  Name</label>
                                    <input type="text" className="form-control"
                                        id="last_name" aria-describedby="emailHelp"
                                        onChange={this.onChangeUserName}
                                        value={this.state.last_name} />
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Username</label>
                                    <input type="text" className="form-control"
                                        id="username" aria-describedby="emailHelp"
                                        onChange={this.onChangeUserName}
                                        value={this.state.username}/>
                                </div>
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Email address</label>
                                    <input type="email" className="form-control"
                                        id="email" aria-describedby="emailHelp"
                                        onChange={this.onChangeUserName}
                                        value={this.state.email}/>
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Password </label>
                                    <input type="password" className="form-control"
                                        id="password" aria-describedby="emailHelp"
                                        onChange={this.onChangePassword}
                                        value={this.state.password}/>
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label>User Groups</label>
                                    <select className="form-control" multiple id="UserGroup" onChange={this.onChangeRole} value={this.state.status===true ? 'Active':'Deactive'}>
                                        <option value="">Select Groups</option>
                                        <option value="Managerial">Managerial</option>
                                        <option value="Admin">Admin</option>
                                        <option value="Executive">Executive</option>
                                    </select>

                                </div>
                            </div>
                            <div className="col-md-12 mt-2">
                                <hr />
                                <div className="form-group text-center">

                                    {this.props.editFlag === true ?
                                        <button type="button" className="btn btn-info btn-base " onClick={this.updateDepttData}
                                        >
                                            Submit
                                        </button> : ''
                                    }

                                    {this.props.addFlag === true ?
                                        <button type="button" className="btn btn-info btn-base" onClick={this.addDeptData}
                                        >
                                            Submit
                                      </button> : ''
                                    }

                                </div>
                            </div>
                        </div>

                    </form>

                </BootstrapModal>
            </div>
        );
    }
}

export default UserAddEdit;
