import React, { Component } from 'react';
import BootstrapModal from '../partials/BootstrapModal';

class UnitManagementAddEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            status: true
        };
        this.onChangeUnitName = this.onChangeUnitName.bind(this);
        this.onChangeStatu = this.onChangeStatu.bind(this);
    }

    onChangeUnitName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeStatu(e) {
        console.log(e.target.value);
        if (e.target.value === 'Active') {
            this.setState({
                status: true
            });
        } else {
            this.setState({
                status: false
            });
        }

    }

    render() {
        return (
            <div>
                <BootstrapModal></BootstrapModal>
            </div>
        );
    }
}

export default UnitManagementAddEdit;
