import React, { Component } from 'react';
// import axios from 'axios';
import ClientAddEdit from './ClientAddEdit';
import axiosInstance from '../../intercept';



class ClientManagement extends Component {
    constructor(props) {
        const token = localStorage.getItem("token")

        super(props);
        this.state = {
            clientData: [],
            addModalShow: false,
            // addModalClose: false,
            editId: null,
            editFlag: false,
            addFlag: false

        }

    }



    componentDidMount() {
        this.loadUsers();
    }



    loadUsers = () => {

        const token = JSON.parse(window.localStorage.getItem('token'))
        // console.log(token.token);
        if (token) {

            axiosInstance.get('/clients/', {
                headers: {
                    'Authorization': `token ${token.token}`
                }

            })
                .then((res) => {
                    // console.log(res.data)
                    this.setState({
                        clientData: res.data.reverse()
                    })

                })
                .catch((error) => {
                    console.error(error)
                })
        } else {
            alert('No token here')
        }
    }


    deleteUser = async id => {
        const token = JSON.parse(window.localStorage.getItem('token'))
        if (token) {
            await axiosInstance.delete(`/clients/${id}`, {

                headers: {
                    'Authorization': `token ${token.token}`
                }
            }
            );
            this.loadUsers();
        };

    }

    open = () => {
        alert('open')
        this.setState({
            addModalShow: true
        })

    }
    // componentDidUpdate() {
    //     this.loadUsers();
    // }
    // {`${id}`}
    clientEdit = async(id) => {
        // alert('edit', id)
        // console.log(id);
        this.setState({
            editId: id,
            editFlag:true
        })
