import React, { Component } from 'react';
import axios from 'axios';

class ClientManagement extends Component {
    constructor(props) {
        const token = localStorage.getItem("token")
        super(props);
        this.state = {
            clientData: [],
            client_name: "",
            address: "",
            contact_info: "",
            email_address: "",
            phone: "",
            status: ""

        }
        this.onChangeClientName = this.onChangeClientName.bind(this);
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.onChangeContact = this.onChangeContact.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePhone = this.onChangePhone.bind(this);
        this.onChangeStatus = this.onChangeStatus.bind(this);
    }

    onChangeClientName(e) {
        this.setState({
            client_name: e.target.value
        });
    }

    onChangeAddress(e) {
        this.setState({
            address: e.target.value
        });
    }

    onChangeContact(e) {
        console.log(e.target.value);
        this.setState({
            contact_info: e.target.value
        });
    }

    onChangeEmail(e) {
        this.setState({
            email_address: e.target.value
        });
    }

    onChangePhone(e) {
        this.setState({
            phone: e.target.value
        });
    }

    onChangeStatus(e) {
        console.log(e.target.value);
        this.setState({
            status: e.target.value
        });
    }
    componentDidMount() {
        this.loadUsers();

    }

    loadUsers = () => {
        const token = JSON.parse(window.localStorage.getItem('token')) 
        if (token) {
            const clientData =  axios.get('http://0.0.0.0:8000/api/clients/', {
                headers: {
                  'Authorization': `token ${token.token}`
                }
              })
              .then((res) => {
                //   console.log(res.data)
                  this.setState({
                      clientData:res.data
                  })
              })
              .catch((error) => {
                console.error(error)
              })
        }

    }
    addClientData = (e) => {
        alert('hi')
        e.preventDefault()
        const obj = {
            client_name: this.state.client_name,
            address: this.state.address,
            contact_info: this.state.contact_info,
            email_address: this.state.email_address,
            phone: this.state.phone,
            status: this.state.status
          };
        console.log('data test', obj);

        if (token) {
            const clientData =  axios.post('http://0.0.0.0:8000/api/clients/', {
                headers: {
                  'Authorization': `token ${token.token}`
                }
              })
              .then((res) => {
                //   console.log(res.data)

              })
              .catch((error) => {
                console.error(error)
              })
        }

    }

    render() {

        console.log(this.state.client_name);
        console.log(this.state.address);
        return (
            <div>
                <div className="layout_content">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="accordionform">
                                    <div className="accordion" id="accordionExample">
                                        {/* CM form */}
                                        <div className="card table-card">
                                            <div className="card-header" id="headingOne">
                                                <h2 className="mb-0">
                                                </h2><h3 className="float-left"><i id="addIcon" className="fa fa-chevron-circle-down" style={{ font: 15 }} /> Client management
                  </h3>
                                                <button type="button" data-toggle="collapse" data-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne" className="btn btn-sm btn-info btn-base float-right">
                                                    <i className="fa fa-plus-square-o" />&nbsp;
                    Add New
                  </button>
                                            </div>
                                            <div id="collapseOne" className="collapse" aria-labelledby="headingOne" data-parent="#accordionExample">
                                                <div className="card-body">
                                                    <form onSubmit={this.addClientData}>
                                                        <div className="form-row">
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleInputEmail1"> Client Name</label>
                                                                    <input type="text" className="form-control"
                                                                        id="client_name"
                                                                        aria-describedby="emailHelp"
                                                                        onChange={this.onChangeClientName}
                                                                        value={this.state.client_name}/>
                                                                </div>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleInputEmail1"> Address</label>
                                                                    <input type="text" className="form-control"
                                                                        id="address" aria-describedby="emailHelp"
                                                                        onChange={this.onChangeAddress}
                                                                        value={this.state.address}
                                                                    />
                                                                </div>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleInputEmail1">Contact Info</label>
                                                                    <input type="text" className="form-control"
                                                                        id="contact_info" aria-describedby="emailHelp"
                                                                        onChange={this.onChangeContact}
                                                                        value={this.state.contact_info}/>
                                                                </div>
                                                            </div>
                                                        </div>
                                                        <div className="form-row">
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleInputEmail1">Emial address</label>
                                                                    <input type="email" className="form-control"
                                                                        id="email_address" aria-describedby="emailHelp"
                                                                        onChange={this.onChangeEmail}
                                                                        value={this.state.email_address}/>
                                                                </div>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleInputEmail1">Phone no </label>
                                                                    <input type="text" className="form-control"
                                                                        id="phone" aria-describedby="emailHelp"
                                                                        onChange={this.onChangePhone}
                                                                        value={this.state.phone}/>
                                                                </div>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <div className="form-group">
                                                                    <label htmlFor="exampleFormControlSelect1">Status</label>
                                                                    <select className="form-control" id="exampleFormControlSelect1" onChange={this.onChangeStatus}>
                                                                        <option >Select Status</option>
                                                                        <option value="Active">Active</option>
                                                                        <option value= "Deactive">Deactive</option>
                                                                    </select>
                                                                </div>
                                                            </div>
                                                        </div>
                                                        <div className="form-row">
                                                            <div className="form-group col-md-12">
                                                                <button type="submit" className="btn btn-sm btn-info btn-base float-right">
                                                                    Submit
                          </button>
                                                            </div>
                                                            <div className="form-group col-md-6">
                                                            </div>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card table-card">
                                    {/* <div class="card-header">
                              <h3 class="float-left"><i class="fa fa-info-circle"></i> Table Title</h3>
                              <button class="btn btn-sm btn-info btn-base float-right"><i class="fa fa-plus-square-o"></i>&nbsp; Add New </button>
                          </div> */}
                                    <div className="card-body">
                                        <div className="table-responsive">
                                            <table id="example" className="table table-striped table-bordered" style={{ width: '100%' }}>
                                                <thead>
                                                    <tr>
                                                        <th>Client Name</th>
                                                        <th>Address</th>
                                                        <th>Contact Info</th>
                                                        <th>Email</th>
                                                        <th>Phone No</th>
                                                        <th>Status</th>
                                                        <th>Action</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {
                                                        this.state.clientData.map((clien_data, index) => 
                                                        <tr key={index}>
                                                        <td>{clien_data.name}</td>
                                                        <td>{clien_data.address}</td>
                                                        <td>{clien_data.contact_info}</td>
                                                        <td>{clien_data.email}</td>
                                                        <td>{clien_data.phone_no}</td>
                                                        <td><span className="approved">Active</span></td>
                                                        <td className="text-nowrap">
                                                            <div>
                                                                <button className="btn btn-sm btn-del mr10" data-toggle="modal" data-target="#">
                                                                    <i className="fa fa-trash-o" />
                                                                </button>
                                                                <button className="btn btn-sm btn-edit" data-toggle="modal" data-target="#formModal">
                                                                    <i className="fa fa-pencil" />
                                                                </button>
                                                            </div>
                                                        </td>
                                                    </tr>
                                                        )
                                                    }

                                                </tbody>
                                                <tfoot>
                                                    <tr>
                                                        <th>Client Name</th>
                                                        <th>Address</th>
                                                        <th>Contact Info</th>
                                                        <th>Email</th>
                                                        <th>Phone No</th>
                                                        <th>Status</th>
                                                        <th width="80px">Action</th>
                                                    </tr>
                                                </tfoot>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal fade" id="formModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                <div className="modal-dialog modal-lg" role="document">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title" id="exampleModalLabel">
                                                Edit Client
                </h5>
                                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                                <span aria-hidden="true">
                                                    ×
                  </span>
                                            </button>
                                        </div>
                                        <div className="modal-body">
                                            <form>
                                                <div className="form-row">
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1"> Client Name</label>
                                                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                                        </div>
                                                    </div>
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1"> Address</label>
                                                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                                        </div>
                                                    </div>
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1">Contact Info</label>
                                                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="form-row">
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1">Emial address</label>
                                                            <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                                        </div>
                                                    </div>
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1">Phone no </label>
                                                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                                        </div>
                                                    </div>
                                                    <div className="col-md-4">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleFormControlSelect1">Status</label>
                                                            <select className="form-control" id="exampleFormControlSelect1">
                                                                <option value= "">Active</option>
                                                                <option value= "">Deactive</option>
                                                            </select>
                                                        </div>
                                                    </div>
                                                </div>
                                            </form>
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" data-dismiss="modal">
                                                Close
                </button>
                                            <button type="button" className="btn btn-info btn-base">
                                                Save changes
                </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}

export default ClientManagement;
