import React, { Component } from 'react';
import axiosInstance from '../../intercept';
import BootstrapModal from '../partials/BootstrapModal';
import ModalHeader from '../partials/ModalHeader';

class DepartmentAddEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            status: true
        };
        this.onChangeDeptName = this.onChangeDeptName.bind(this);
        this.onChangeStatus = this.onChangeStatus.bind(this);
    }

    onChangeDeptName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeStatus(e) {
        console.log(e.target.value);
        if (e.target.value === 'Active') {
            this.setState({
                status: true
            });
        } else {
            this.setState({
                status: false
            });
        }

    }

    addDeptData = async(e) => {
        alert('hi')
        e.preventDefault()
        const obj = {
            name: this.state.name,
            status: this.state.status

        };

        console.log('data test', obj);
        const token = JSON.parse(window.localStorage.getItem('token'))
        console.log(token.token);
        if (token) {

           await axiosInstance.post('/departments/', obj, {
                headers: {
                    'Authorization': `token ${token.token}`
                }

            })
                .then((res) => {
                    // console.log(res.data)

                })
                .catch((error) => {
                    console.error(error)
                })
        } else {
            alert('Invalid token')
        }

        this.setState({
            name: "",
            status: ""
        })

    }


    render() {
        return (
            <div>
               <BootstrapModal/>
            </div>
        );
    }
}

export default DepartmentAddEdit;
