//     config.headers = { ...config.headers, Authorization: 'Token '+token.token };
//     // you can also do other modification in config
//     return config;
//   }, function (error) {
//     return Promise.reject(error);
//   }); 
// }

const axiosLoginInstance = axiosInstance.interceptors.request.use(function (config) {
    config.headers = { ...config.headers, Authorization: 'Token '+token.token };
    // you can also do other modification in config
    return config;
  }, function (error) {
    return Promise.reject(error);
}); 

export default { axiosInstance, axiosLoginInstance };
