import React, { Component } from 'react';
import axiosInstance from '../../intercept';
import BootstrapModal from '../partials/BootstrapModal';


class DepartmentAddEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            status: "Active",
            error_message: {

            },
            msg_success: "",
            modalTitle:(this.props.addFlag=== true? 'Department Add' : 'Department Edit')

        };
        this.onChangeDeptName = this.onChangeDeptName.bind(this);
        this.onChangeStatus = this.onChangeStatus.bind(this);
    }

    onChangeDeptName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeStatus(e) {
        console.log(e.target.value);
        if (e.target.value === 'Active') {
            this.setState({
                status: true
            });
        } else {
            this.setState({
                status: false
            });
        }

    }

    addDeptData = async (e) => {

        e.preventDefault()
        const obj = {
            name: this.state.name,
            status: (this.state.status === 'Active' ? true : false)

        };

        console.log('data test', obj);
        const token = JSON.parse(window.localStorage.getItem('token'))
        console.log(token.token);
        if (token) {

            await axiosInstance.post('/departments/', obj, {
                headers: {
                    'Authorization': `token ${token.token}`
                }

            })
                .then((res) => {
                    if (res.status === 201) {
                        this.setState({
                            name: "",
                            msg_success: "Data Added Successfully"
                        })
                    }

                })
                .catch((error) => {
                    // console.error(error)
                    this.setState({
                        error_message: error.response.data
                    })
                })
        } else {
            alert('Invalid token')
        }

        this.setState({
            name: "",
            status: ""
        })

        this.props.loadUsers()
    }



    render() {
        return (
            <div>
                <BootstrapModal openModal={this.props.openModal} modalHandler={this.props.modalHandler} modalTitle={this.state.modalTitle}>
                    <form>
                        <div className="form-row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Department Name</label>
                                    <input type="text" className="form-control"
                                        id="exampleInputEmail1" aria-describedby="emailHelp"
                                        onChange={this.onChangeDeptName}
                                        value={this.state.name} />
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="exampleFormControlSelect1">Status</label>
                                    <select className="form-control" onChange={this.onChangeStatus} defaultValue={this.state.status}>
                                        <option value="Active">Active</option>
                                        <option value="Deactive">Deactive</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    </form>
                </BootstrapModal>
            </div>
        );
    }
}

export default DepartmentAddEdit;
