import React, { Component } from 'react';
import BootstrapModal from '../partials/BootstrapModal';
import axiosInstance from '../../intercept';
import Select from 'react-select'

class TeamManagementAddEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error_message: {},
            msg_success: "",
            id: null,
            modalTitle: (this.props.addFlag === true ? 'Team Add' : 'Team Edit')
        };
    }


    addUsertData = async (e) => {

        e.preventDefault()
        const obj = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password,
            groups: this.state.id

        };

        console.log('data test', obj);
        const token = JSON.parse(window.localStorage.getItem('token'))
        console.log(token.token);
        if (token) {

            await axiosInstance.post('/users/', obj, {
                headers: {
                    'Authorization': `token ${token.token}`
                }

            })
                .then((res) => {
                    if (res.status === 201) {
                        this.setState({

                            msg_success: "Data Added Successfully"
                        })
                    }

                })
                .catch((error) => {
                    // console.error(error)
                    this.setState({
                        error_message: error.response.data
                    })
                })
        } else {
            alert('Invalid token')
        }

        this.setState({
            first_name: "",
            last_name: "",
            username: "",
            email: "",
            password: ""

        })

        this.props.loadUsers()
    }


    async getOptions() {
        const token = JSON.parse(window.localStorage.getItem('token'))

        if (token) {
            const res = await axiosInstance.get('/team-list//',
            {
                headers: {
                    'Authorization': `token ${token.token}`
                }

                })

            const data = res.data

            const options = data.map(d => ({
              "value" : d.id,
              "label" : d.name

            }))

            this.setState({groups: options})

          }
            else {
            alert('No token here')
        }


      }



      componentDidMount(){
          this.getOptions()
      }

    render() {
        return (
            <div>
                <BootstrapModal openModal={this.props.openModal} modalHandler={this.props.modalHandler} modalTitle={this.state.modalTitle}>
                    <form>
                        <div className="form-row">
                            <div className="col-md-4">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1"> Name</label>
                                    <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                </div>
                            </div>
                            <div className="col-md-4">
                                <label htmlFor="exampleInputEmail1">Members</label>
                                {/* <br /> */}

                                <Select options={this.state.groups} onChange={this.onChangeGroupName} isMulti isSearchable />
{/* 
                                <select className="selectpicker" multiple data-live-search="true">
                                    <option value="Mu">Munna</option>
                                    <option value="Ha">Hasan</option>
                                    <option value="Km">kamrul</option>
                                    <option value="Em">Emon</option>
                                    <option value="Ra">Rasel</option>
                                </select> */}
                            </div>
                            <div className="col-md-4">
                                <div className="ml-5">
                                    <div className="form-group">
                                        <label htmlFor="phone">
                                            Status
                          </label>
                                        <div className="area-ckeckbox-radio">
                                            <div className="radio radio-success radio-inline ">
                                                <input type="radio" id="inlineRadio3" defaultValue="option3" name="radioInline" defaultChecked />
                                                <label htmlFor="inlineRadio3">
                                                    Active
                              </label>
                                            </div>
                                            <div className="radio radio-danger radio-inline ml15">
                                                <input type="radio" id="inlineRadio4" defaultValue="option4" name="radioInline" />
                                                <label htmlFor="inlineRadio4">
                                                    Deactive
                              </label>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="col-md-12 mt-2">
                                <hr />
                                <div className="form-group text-center">

                                    {this.props.editFlag === true ?
                                        <button type="button" className="btn btn-info btn-base " onClick={this.updateDepttData}
                                        >
                                            Submit
                                        </button> : ''
                                    }

                                    {this.props.addFlag === true ?
                                        <button type="button" className="btn btn-info btn-base" onClick={this.addUsertData}
                                        >
                                            Submit
                                      </button> : ''
                                    }

                                </div>
                            </div>
                        </div>
                    </form>
                </BootstrapModal>
            </div>
        );
    }
}

export default TeamManagementAddEdit;
